mapper.select:
SELECT COMCODE, COMNAME, COMREP, COMTEL, DEL FROM (SELECT ROW_NUMBER()OVER(ORDER BY COMCODE ASC) AS RNUM, COMCODE, COMNAME, COMREP, COMTEL, DEL FROM TB_COMPANY WHERE 1=1
AND COMCODE LIKE '%' || #{search} || '%'
AND COMNAME LIKE '%' || #{search} || '%'
AND COMTEL LIKE '%' || #{search} || '%'
AND COMREP = #{search}
ORDER BY COMCODE ASC) WHERE RNUM BETWEEN ${start} AND ${end}


SELECT COMCODE, COMNAME, COMREP, COMTEL, DEL
FROM TB_COMPANY
WHERE COMCODE =#{comCode}


SELECT NVL(COUNT(*),0)
FROM TB_COMPANY
WHERE COMNAME =#{comName}

mapper.update:
UPDATE TB_COMPANY
SET COMNAME= #{comName}, COMREP= #{comRep}, COMTEL= #{comTel}
WHERE COMCODE= #{comCode}

UPDATE TB_COMPANY 
SET DEL = 1
WHERE COMCODE = #{comCode}
